mapper.select:
select salary_id, s.emp_id,a.emp_name,base_salary, achievements, bonus, check_deduction, sum_salary, salary_month, salary_status, s.ext_a, s.ext_b, s.ext_c from salary s LEFT JOIN achives a ON s.emp_id=a.emp_id
and a.emp_name like CONCAT('%',#{empName},'%')
and DATE_FORMAT(salary_month,'%Y-%m')=#{salaryMonth}
